    End Sub

    Shared Sub Main()
        Application.Run(New Form1())
    End Sub			

    Private Sub NavigateToPage()
		' Open document in viewer.
		MyPdfViewer.Open("C:\MyDocument.pdf")

		' Move to last page.
		MyPdfViewer.GoToLastPage()
	End Sub

End Class

