		{
			this.pdfViewer = new PdfViewer();
			this.Controls.Add(pdfViewer);
			this.pdfViewer.ExceptionOccurred += pdfViewer_ExceptionOccurred;
		}

		void pdfViewer_ExceptionOccurred(object sender, ExceptionOccurredEventArgs e)
